def h_count(handles)
  #La méthode .count appliquée à l'array handles compte le nombre d'éléments. Aurait aussi marché avec .length et .size
  puts '-' * 10
  puts "Il y a #{handles.count} handles dans cette liste"
end

def h_min_length(handles)
  #Ma compréhension : La méthode .min_by crée un hash, leur assigne une valeur (ici handle.length) puis parcours chaque key (ici handle) pour trouver la valeur min
  puts '-' * 10
  min_handle = handles.min_by{|handle| handle.length}
  puts "Le handle le plus court est #{min_handle}"
end

def h_length_5(handles)
  #La méthode .count appliquée à l'array handles_short compte le nombre d'éléments selon une condition (ici que la longueur des handles soit 5)
  puts '-' * 10
  handles_5 = handles.count {|handle| handle.length == 6}
  puts "Il y a #{handles_5} handles de 5 caractères (sans compter le @)"
end

def h_capital(handles)
  #La méthode .count appliquée à l'array handles_short compte le nombre d'éléments selon une condition (ici que le handle commence par une lettre majuscule après le @)
  puts '-' * 10
  handles_capital = handles.count {|handle| handle =~ /^@[A-Z]/}
  puts "Il y a #{handles_capital} handles commençant par une majuscule (sans compter le @)"
end

def h_sorted(handles)
  #La méthode .sort seule permet de trier par ordre alphabétique
  puts '-' * 10
  handles_sorted_a = handles.sort
  puts "Les 5 premiers handles triés alphabétiquement sont #{handles_sorted_a[0..4]}"
end

def h_sorted_length(handles)
#La méthode .sort permet de trier par ordre de longueur selon une condition
  puts '-' * 10
  handles_sorted_length = handles.sort_by{|handle| handle.length}
  puts "Les 5 premiers handles triés par longueurs sont #{handles_sorted_length[0..4]}"
end

def h_position_epenser(handles)
  #La méthode .index permet d'indiquer la position de l'argument dans l'array
  puts '-' * 10
  puts "La position de la personne @epenser dans l'array est #{handles.index("@epenser")}"
end

def h_repartition(handles)
  #Boucle for avec les éléments expliqués ci-dessus
  puts '-' * 10
  min_h = handles.min_by{|handle| handle.length}
  max_h = handles.max_by{|handle| handle.length}
  
  for i in min_h.length..max_h.length do
    repartition = handles.count {|handle| handle.length == i}
    if repartition == 1 
      then
      puts "Il y a #{repartition} handle de #{i} lettres (sans compter le @)"
    elsif
      repartition != 0 
      then
      puts  "Il y a #{repartition} handles de #{i} lettres (sans compter le @)"
    end
  end
end

def perform
  handles = get_handles
  h_count(handles)
  h_min_length(handles)
  h_length_5(handles)
  h_capital(handles)
  h_sorted(handles)
  h_sorted_length(handles)
  h_position_epenser(handles)
  h_repartition(handles)
end

perform